import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CardFetcher.css';


function CardFetcher() {
    const [cardData, setCardData] = useState(null);

    useEffect(() => {
        const fetchCard = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/card/Primeval%20Titan/m11`);
                setCardData(response.data);
            } catch (error) {
                console.error("Error fetching card:", error);
            }
        };

        fetchCard();
    }, []);  // Run once when the component mounts

    function handleDragStart(event) {
        event.dataTransfer.setData("cardId", event.target.id);

        // Create a clone of the image
        const dragImage = event.target.cloneNode(true);
        dragImage.style.position = 'absolute';
        dragImage.style.top = '-9999px'; 
        document.body.appendChild(dragImage);

        // Set the cloned image as the drag image
        event.dataTransfer.setDragImage(dragImage, 50, 50); 

        // Remove the cloned image after the drag operation is complete
        event.dataTransfer.addEventListener('dragend', () => {
            document.body.removeChild(dragImage);
        });
    }    

   function handleDragOver(event) {
       event.preventDefault();
   }

   function handleDrop(event) {
       event.preventDefault();
       const cardId = event.dataTransfer.getData("cardId");
       const card = document.getElementById(cardId);
       event.target.appendChild(card);
   }

   return (
       <div>
           <div className="grid-container">
               {Array(9).fill(null).map((_, index) => (
                 <div key={index} className="grid-item" onDrop={handleDrop} onDragOver={handleDragOver}></div>
               ))}
           </div>
           {cardData && (
               <div className="art-container">
                   <img src={cardData.image_uris.art_crop} alt="Primeval Titan" draggable="true" onDragStart={handleDragStart} id="primeval-titan"/>
               </div>
           )}
       </div>
   );
}

export default CardFetcher;
