import React, { useState } from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';
import './CardFetcher.css';

function CardFetcher() {
    const [searchResults, setSearchResults] = useState([]);

    const handleSearch = async (query) => {
        try {
            const response = await axios.get(`http://localhost:5000/search/${query}`);
            setSearchResults(response.data);  // assuming the backend returns an array of cards
        } catch (error) {
            console.error("Error searching for cards:", error);
        }
    };

    function handleDragStart(event) {
        event.dataTransfer.setData("cardId", event.target.id);
    }

    function handleDragOver(event) {
        event.preventDefault();
    }

    function handleDrop(event) {
        event.preventDefault();
        const cardId = event.dataTransfer.getData("cardId");

        while (event.target.firstChild) {
            event.target.removeChild(event.target.firstChild);
        }

        // Fetch the data associated with this card ID from the search results.
        const cardData = searchResults.find(card => card.id.toString() === cardId);
        if (cardData && cardData.image_uris && cardData.image_uris.art_crop) {
            const newImage = document.createElement("img");
            newImage.src = cardData.image_uris.art_crop;
            newImage.alt = cardData.name;
            newImage.className = "cropped-image";
            newImage.draggable = "true";
            newImage.id = cardData.id;
            newImage.addEventListener('dragstart', handleDragStart);

            event.target.appendChild(newImage);
        }
    }


    return (
        <div>
            <SearchBar onSearch={handleSearch} />
            <div className="grid-container">
                {Array(9).fill(null).map((_, index) => (
                    <div key={index} className="grid-item" onDrop={handleDrop} onDragOver={handleDragOver}></div>
                ))}
            </div>
            {searchResults.map(card => (
                card.image_uris && card.image_uris.art_crop ? (
                    <div className="art-container" key={card.id}>
                        <img className="cropped-image" src={card.image_uris.art_crop} alt={card.name} draggable="true" onDragStart={handleDragStart} id={card.id} />
                    </div>
                ) : (
                    <div className="art-container" key={card.id}>
                        <p>No image available for {card.name}.</p>
                    </div>
                )
            ))}
        </div>
    );

}

export default CardFetcher;
