import React, { useState } from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';
import './CardFetcher.css';

function CardFetcher() {
    const [searchResults, setSearchResults] = useState([]);
    const [gridItems, setGridItems] = useState(Array(9).fill(null));

    const handleSearch = async (query) => {
        try {
            const response = await axios.get(`http://localhost:5000/search/${query}`);
            setSearchResults(response.data);
        } catch (error) {
            console.error("Error searching for cards:", error);
        }
    };

    function handleDragStart(event) {
        event.dataTransfer.setData("cardId", event.target.id);
        event.dataTransfer.setData("source", event.target.getAttribute("data-source"));
    }

    function handleDragOver(event) {
        event.preventDefault();
    }

    function handleDrop(event, index) {
        event.preventDefault();
        const cardId = event.dataTransfer.getData("cardId");
        const source = event.dataTransfer.getData("source");

        const updatedGridItems = [...gridItems];

        if (source === "searchResult") {
            setSearchResults(prev => prev.filter(card => card.id.toString() !== cardId));
        } else if (source === "gridItem") {
            const sourceIndex = gridItems.findIndex(item => item && item.id.toString() === cardId);
            if (updatedGridItems[index]) {
                updatedGridItems[sourceIndex] = updatedGridItems[index];
            } else {
                updatedGridItems[sourceIndex] = null;
            }
        }

        const card = searchResults.find(card => card.id.toString() === cardId);
        updatedGridItems[index] = card;
        setGridItems(updatedGridItems);
    }

    return (
        <div>
            <SearchBar onSearch={handleSearch} />
            <div className="grid-container">
                {gridItems.map((card, index) => (
                    <div key={index} className="grid-item" onDrop={(e) => handleDrop(e, index)} onDragOver={handleDragOver}>
                        {card && card.image_uris && card.image_uris.art_crop && (
                            <img 
                                className="cropped-image" 
                                src={card.image_uris.art_crop} 
                                alt={card.name}
                                draggable="true" 
                                onDragStart={handleDragStart} 
                                id={card.id.toString()}
                                data-source="gridItem"
                            />
                        )}
                    </div>
                ))}
            </div>
            {searchResults.map(card => (
                card.image_uris && card.image_uris.art_crop ? (
                    <div className="art-container" key={card.id}>
                        <img 
                            className="cropped-image"
                            src={card.image_uris.art_crop}
                            alt={card.name}
                            draggable="true"
                            onDragStart={handleDragStart}
                            id={card.id.toString()}
                            data-source="searchResult"
                        />
                    </div>
                ) : (
                    <div className="art-container" key={card.id}>
                        <p>No image available for {card.name}.</p>
                    </div>
                )
            ))}
        </div>
    );
}

export default CardFetcher;
