import React, { useState } from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';
import './CardFetcher.css';

function CardFetcher() {
    const [searchResults, setSearchResults] = useState([]);

    const handleSearch = async (query) => {
        try {
            const response = await axios.get(`http://localhost:5000/search/${query}`);
            setSearchResults(response.data);  // assuming the backend returns an array of cards
        } catch (error) {
            console.error("Error searching for cards:", error);
        }
    };

    function handleDragStart(event) {
        event.dataTransfer.setData("cardId", event.target.id);
    }

    function handleDragOver(event) {
        event.preventDefault();
    }

    function handleDrop(event) {
        event.preventDefault();
        const cardId = event.dataTransfer.getData("cardId");
        const card = document.getElementById(cardId);
        event.target.appendChild(card);
    }

    return (
        <div>
       <SearchBar onSearch={handleSearch} />
       <div className="grid-container">
           {Array(9).fill(null).map((_, index) => (
             <div key={index} className="grid-item" onDrop={handleDrop} onDragOver={handleDragOver}></div>
           ))}
       </div>
       {cardData && cardData.image_uris && cardData.image_uris.art_crop ? (
           <div className="art-container">
               <img className="cropped-image" src={cardData.image_uris.art_crop} alt={cardData.name || 'Card Image'} draggable="true" onDragStart={handleDragStart} id="card-image"/>
           </div>
       ) : (
           <p>No card image available.</p>
       )}
   </div>
    );
}

export default CardFetcher;
